from typing import Tuple

def solve(data: str) -> Tuple[str, str]:
    ans1 = 0
    ans2 = None
    for i, c in enumerate(list(data)):
        if c == "(":
            ans1 += 1
        else:
            ans1 -= 1
        if ans1 < 0 and ans2 is None:
            ans2 = i + 1
    return str(ans1), str(ans2)

